def lines_to_df(data, participant_id: str) -> pd.DataFrame:
  """
  data can be either:
    - a list of CSV lines (what you have after .splitlines() / .split('\n'))
    - a raw CSV string
  """
  if isinstance(data, list):
      csv_text = "\n".join([ln for ln in data if ln.strip()])
  else:
      csv_text = str(data).strip()

  df = pd.read_csv(StringIO(csv_text))

  # add participant id (as first column)
  df.insert(0, "participant_id", participant_id)

  # make numeric columns numeric (keeps participant_id as string)
  for c in df.columns:
      if c != "participant_id":
          df[c] = pd.to_numeric(df[c], errors="coerce")

  return df

