"""### Implementing the Continuous Normative Model (CNM)"""

def simulate_single_trial_trajectory(
    prev_belief_L,      # The belief L at the END of the previous trial
    current_LLR,        # The LLR of the star shown on THIS trial
    subjective_H,       # The fitted Subjective H for this user/block
    reaction_time,      # The user's actual RT for this trial (in milliseconds)
    dt_integration_step=0.01 # Renamed dt parameter for clarity
):
    reaction_time_sec = reaction_time / 1000.0

    # 1. Calculate the Prior (The Starting Line)
    # Use Discrete Eq 2 from Glaze to get the "discounted prior"
    # This acts as the initial condition L(t=0) for the continuous simulation
    psi = prev_belief_L + np.log((1 - subjective_H) / subjective_H + np.exp(-prev_belief_L)) \
                        - np.log((1 - subjective_H) / subjective_H + np.exp(prev_belief_L))

    # Handle very short reaction times or zero reaction times gracefully for plotting.
    # We want at least two points to draw a line, unless RT is 0.
    if reaction_time_sec <= 0:
        return np.array([psi]) # Returns a single point (start of decision) for zero/negative RT

    # Determine the number of integration steps. We need n_steps_intervals + 1 points.
    n_steps_intervals = int(reaction_time_sec / dt_integration_step)

    if n_steps_intervals < 1: # If RT is shorter than one dt_integration_step
        n_points = 2 # Force at least 2 points to draw a line
        effective_dt = reaction_time_sec / (n_points - 1) # This will be reaction_time_sec itself
    else:
        n_points = n_steps_intervals + 1 # Include start point and all integration points
        effective_dt = dt_integration_step # Use the user-defined integration step

    L = np.zeros(n_points)
    L[0] = psi  # Initialize the first element with the Prior

    # 3. Continuous Evolution (The Race)
    for t_idx in range(1, n_points):
        L_prev = L[t_idx-1]

        # A. The Leak (Stability Term)
        # Based on Continuous Eq 4: -2 * lambda * sinh(L)
        # We use Subjective H as lambda here
        deterministic_change = -2 * subjective_H * np.sinh(L_prev) * effective_dt

        # B. The Evidence (Drift Term)
        # The star pushes the belief constantly for the duration of the trial
        # We assume the brain "sees" the same LLR continuously
        drift = current_LLR * effective_dt

        # Optional: Add neural noise here (+ sigma * np.sqrt(effective_dt) * np.random.randn()) # Using effective_dt

        # Update
        L[t_idx] = L_prev + deterministic_change + drift

    return L # Returns the full trajectory up to the moment of decision

"""## "Playing Out" The CNM"""

def simulate_single_trial_trajectory_with_threshold(
    prev_belief_L,      # The belief L at the END of the previous trial (for psi calculation)
    current_LLR,        # The LLR of the star shown on THIS trial
    subjective_H,       # The fitted Subjective H for this user/block
    belief_threshold,   # Threshold for making a decision
    max_simulation_duration_ms, # Maximum duration for simulation (ms)
    dt_integration_step # Integration step size (seconds)
):
    max_simulation_duration_sec = max_simulation_duration_ms / 1000.0

    # 1. Calculate the Prior (The Starting Line)
    # Use Discrete Eq 2 from Glaze to get the "discounted prior"
    # This acts as the initial condition L(t=0) for the continuous simulation
    psi = prev_belief_L + np.log((1 - subjective_H) / subjective_H + np.exp(-prev_belief_L)) \
                        - np.log((1 - subjective_H) / subjective_H + np.exp(prev_belief_L))

    # Initialize trajectory storage
    trajectory = [psi]
    time_trajectory = [0.0]

    L_current = psi
    time_current = 0.0

    # 2. Continuous Evolution (The Race)
    while time_current < max_simulation_duration_sec:
        # A. The Leak (Stability Term)
        deterministic_change = -2 * subjective_H * np.sinh(L_current) * dt_integration_step

        # B. The Evidence (Drift Term)
        drift = current_LLR * dt_integration_step

        # Update belief and time
        L_current += deterministic_change + drift
        time_current += dt_integration_step

        trajectory.append(L_current)
        time_trajectory.append(time_current)

        # Check for decision threshold crossing
        if abs(L_current) >= belief_threshold:
            decision = np.sign(L_current) # Decision is made, +1 for L_current > 0, -1 for L_current < 0
            return (time_current * 1000.0, L_current, decision, np.array(trajectory), np.array(time_trajectory) * 1000.0)

    # If max_simulation_duration_sec is reached without a decision, make a decision based on the sign of L_current
    decision = np.sign(L_current) # Make a decision based on the sign of L_current at timeout
    return (max_simulation_duration_ms, L_current, decision, np.array(trajectory), np.array(time_trajectory) * 1000.0)

