"""## Fitting A Hat and Response Time

### Simulation With Belief and Response Time Threshold
"""

def simulate_single_trial_trajectory_with_threshold(
    prev_belief_L,      # The belief L at the END of the previous trial (for psi calculation)
    current_LLR,        # The LLR of the star shown on THIS trial
    subjective_H,       # The fitted Subjective H for this user/block
    belief_threshold,   # Threshold for making a decision
    max_simulation_duration_ms, # Maximum duration for simulation (ms)
    dt_integration_step # Integration step size (seconds)
):
    max_simulation_duration_sec = max_simulation_duration_ms / 1000.0

    # 1. Calculate the Prior (The Starting Line)
    # Use Discrete Eq 2 from Glaze to get the "discounted prior"
    # This acts as the initial condition L(t=0) for the continuous simulation
    psi = prev_belief_L + np.log((1 - subjective_H) / subjective_H + np.exp(-prev_belief_L)) \
                        - np.log((1 - subjective_H) / subjective_H + np.exp(prev_belief_L))

    # Initialize trajectory storage
    trajectory = [psi]
    time_trajectory = [0.0]

    L_current = psi
    time_current = 0.0

    # 2. Continuous Evolution (The Race)
    while time_current < max_simulation_duration_sec:
        # A. The Leak (Stability Term)
        deterministic_change = -2 * subjective_H * np.sinh(L_current) * dt_integration_step

        # B. The Evidence (Drift Term)
        drift = current_LLR * dt_integration_step

        # Update belief and time
        L_current += deterministic_change + drift
        time_current += dt_integration_step

        trajectory.append(L_current)
        time_trajectory.append(time_current)

        # Check for decision threshold crossing
        if abs(L_current) >= belief_threshold:
            decision = np.sign(L_current) # Decision is made, +1 for L_current > 0, -1 for L_current < 0
            return (time_current * 1000.0, L_current, decision, np.array(trajectory), np.array(time_trajectory) * 1000.0)

    # If max_simulation_duration_sec is reached without a decision
    decision = 0 # No decision made (timeout)
    return (max_simulation_duration_ms, L_current, decision, np.array(trajectory), np.array(time_trajectory) * 1000.0)

"""### Setting Up Cost Function"""

def cost_function_cnm(params, analysis_df, dt_integration_step=0.01):
    belief_threshold, max_simulation_duration_ms = params
    total_cost = 0

    # Ensure prev_belief_L starts at 0 for the first trial of each block
    prev_belief_L_map = {block_id: 0.0 for block_id in analysis_df['block_id'].unique()}

    # Iterate through each trial in the DataFrame
    for _, row in analysis_df.iterrows():
        block_id = row['block_id']
        current_LLR = row['LLR']
        subjective_H = row['subjective_h_snapshot'] # Use the snapshot H
        actual_rt_ms = row['reaction_time_ms']
        actual_choice = row['choice'] # 1 for positive, 0 for negative

        # Map actual_choice to +1 or -1 for comparison
        actual_decision_sign = 1 if actual_choice == 1 else -1

        predicted_rt_ms, predicted_belief, predicted_decision_sign, _, _ = simulate_single_trial_trajectory_with_threshold(
            prev_belief_L_map[block_id],
            current_LLR,
            subjective_H,
            belief_threshold,
            max_simulation_duration_ms,
            dt_integration_step
        )

        # 1. Choice Accuracy Cost (Cross-Entropy-like for decisions)
        # predicted_decision_sign will now always be 1 or -1, no special handling for 0 needed
        predicted_decision_sign_for_cost = predicted_decision_sign

        # Penalize incorrect choices
        if predicted_decision_sign_for_cost != actual_decision_sign:
            choice_cost = 1.0 # Fixed penalty for wrong choice
        else:
            choice_cost = 0.0

        # 2. Reaction Time Cost (Squared Error)
        rt_cost = ((predicted_rt_ms - actual_rt_ms) / 1000.0)**2 # Scale RT difference for cost

        # Accumulate costs
        total_cost += choice_cost + rt_cost

        # Update prev_belief_L_map for the next trial in the same block
        # For cost function, we use the *actual* observed belief_L at the end of the trial
        # to ensure the model evaluates performance against observed history, not its own predictions.
        prev_belief_L_map[block_id] = row['belief_L']

    return total_cost

"""### Minimize Error"""

from scipy.optimize import minimize

# Step 3: Estimate A_hat and Max Timeout
# Filter out rows with NaN in subjective_h_snapshot or belief_L before optimization
filtered_analysis_df = analysis_df.dropna(subset=['subjective_h_snapshot', 'belief_L', 'LLR', 'reaction_time_ms', 'choice']).copy()

# Define bounds for parameters
# Belief threshold should be positive, max_simulation_duration_ms should be positive
min_belief_threshold = 0.1
max_belief_threshold = 15.0 # Max typical belief L is around 15 in Glaze model
min_max_sim_duration_ms = 100 # Minimum reasonable RT
max_max_sim_duration_ms = filtered_analysis_df['reaction_time_ms'].max() * 2 # Up to 2x max observed RT as a heuristic

bounds = [
    (min_belief_threshold, max_belief_threshold),
    (min_max_sim_duration_ms, max_max_sim_duration_ms)
]

# Initial guess for the parameters
initial_guess = [5.0, filtered_analysis_df['reaction_time_ms'].mean()] # A_hat = 5, Max RT = avg RT

print("Optimizing parameters...")
optimization_result = minimize(
    cost_function_cnm,
    initial_guess,
    args=(filtered_analysis_df, 0.01),
    method='L-BFGS-B', # A good choice for bounded, unconstrained problems
    bounds=bounds
)

optimal_belief_threshold, optimal_max_simulation_duration_ms = optimization_result.x
print(f"Optimization successful: {optimization_result.success}")
print(f"Optimal Belief Threshold (A_hat): {optimal_belief_threshold:.3f}")
print(f"Optimal Max Simulation Duration (ms): {optimal_max_simulation_duration_ms:.3f}")
print(f"Final Cost: {optimization_result.fun:.3f}")

